var names_without_a = [];
// write your code here 
console.log(names_without_a);

exercise_2.solution:
var names = ['rie' , 'asako', 'stefano', 'kyoko', 'lia'];
var names_without_a = [];
for( var i = 0; i < names.length; i++ ){
	if( names[i].indexOf('a') === -1){
		names_without_a.push(names[i]);
	}
}
console.log(names_without_a);
